/**
 * SVG Icon Helper
 * 
 * Replaces Material Symbols icon font with inline SVGs for better performance.
 * Eliminates ~200KB font download.
 */

class Icons
{
    /**
     * Get an inline SVG icon
     * 
     * @param string $name Icon name (material symbols name)
     * @param string $class Additional CSS classes
     * @return string SVG HTML
     */
    public static function get(string $name, string $class = ''): string
    {
        $icons = self::getIcons();

        if (!isset($icons[$name])) {
            // Fallback to material symbols if icon not found
            return '<span class="material-symbols-outlined ' . htmlspecialchars($class) . '">' . htmlspecialchars($name) . '</span>';
        }

        $baseClass = 'inline-block align-middle';
        $sizeClass = 'w-6 h-6'; // 24px default

        // Handle size classes
        if (strpos($class, 'text-lg') !== false || strpos($class, 'text-base') !== false) {
            $sizeClass = 'w-5 h-5';
        } elseif (strpos($class, 'text-xl') !== false) {
            $sizeClass = 'w-6 h-6';
        } elseif (strpos($class, 'text-2xl') !== false) {
            $sizeClass = 'w-7 h-7';
        } elseif (strpos($class, 'text-3xl') !== false) {
            $sizeClass = 'w-8 h-8';
        } elseif (strpos($class, 'text-sm') !== false) {
            $sizeClass = 'w-4 h-4';
        }

        // Extract color classes
        $colorClass = '';
        if (preg_match('/text-[a-z]+-\d+/', $class, $matches)) {
            $colorClass = $matches[0];
        }

        $finalClass = trim("$baseClass $sizeClass $colorClass");

        return '<svg class="' . $finalClass . '" fill="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">' . $icons[$name] . '</svg>';
    }

